svg.script:
    var KEY = { w:87, a:65, s:83, d:68 };
    var moveSpeed = 5;
    var circle = document.getElementsByTagName("circle")[0];
    var x = circle.getAttribute('cx')*1,
        y = circle.getAttribute('cy')*1;
    var rect = document.getElementsByTagName("rect")[0];
    var rx = rect.getAttribute('x')*1,
        ry = rect.getAttribute('y')*1;
	var green = "green";
//	var visib = "visible"
    document.documentElement.addEventListener('keydown',function(evt){
//	  if (visib === "visible") {
//	      visib = "hidden"
//	  } else {
//	      visib = "visible"
//	  }
//	  rect.setAttribute('visible', visib)
      switch (evt.keyCode){
//	  document.getElementById("rect").style.visibility = "hidden";
        case KEY.w:
          circle.setAttribute('cy',y-=moveSpeed);
          // Alternatively:
          // circle.cy.baseVal.value = (y-=moveSpeed);
        break;
        case KEY.s:
          circle.setAttribute('cy',y+=moveSpeed);
          rect.setAttribute('y',y+=moveSpeed);
          rect.setAttribute('style',"fill:green");
          rect.setAttribute('width',30);

        break;
        case KEY.a:
          circle.setAttribute('cx',x-=moveSpeed);
        break;
        case KEY.d:
          circle.setAttribute('cx',x+=moveSpeed);
        break;
      }
    },false);
